import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { setupTelegramBot } from "./telegramBot";
import {
  insertCompanySchema,
  insertAccountSchema,
  insertJournalEntrySchema,
  insertJournalEntryDetailSchema,
  insertTelegramSettingsSchema,
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Setup Telegram bot
  setupTelegramBot(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Company routes
  app.get('/api/companies', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const companies = await storage.getCompanies(userId);
      res.json(companies);
    } catch (error) {
      console.error("Error fetching companies:", error);
      res.status(500).json({ message: "Failed to fetch companies" });
    }
  });

  app.get('/api/companies/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const company = await storage.getCompany(id);
      if (!company) {
        return res.status(404).json({ message: "Company not found" });
      }
      res.json(company);
    } catch (error) {
      console.error("Error fetching company:", error);
      res.status(500).json({ message: "Failed to fetch company" });
    }
  });

  app.post('/api/companies', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const companyData = insertCompanySchema.parse({
        ...req.body,
        createdBy: userId,
      });
      const company = await storage.createCompany(companyData);
      res.status(201).json(company);
    } catch (error) {
      console.error("Error creating company:", error);
      res.status(500).json({ message: "Failed to create company" });
    }
  });

  app.put('/api/companies/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const companyData = insertCompanySchema.partial().parse(req.body);
      const company = await storage.updateCompany(id, companyData);
      res.json(company);
    } catch (error) {
      console.error("Error updating company:", error);
      res.status(500).json({ message: "Failed to update company" });
    }
  });

  app.delete('/api/companies/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteCompany(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting company:", error);
      res.status(500).json({ message: "Failed to delete company" });
    }
  });

  // Account routes
  app.get('/api/companies/:companyId/accounts', isAuthenticated, async (req: any, res) => {
    try {
      const companyId = parseInt(req.params.companyId);
      const accounts = await storage.getAccounts(companyId);
      res.json(accounts);
    } catch (error) {
      console.error("Error fetching accounts:", error);
      res.status(500).json({ message: "Failed to fetch accounts" });
    }
  });

  app.get('/api/accounts/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const account = await storage.getAccount(id);
      if (!account) {
        return res.status(404).json({ message: "Account not found" });
      }
      res.json(account);
    } catch (error) {
      console.error("Error fetching account:", error);
      res.status(500).json({ message: "Failed to fetch account" });
    }
  });

  app.post('/api/accounts', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const accountData = insertAccountSchema.parse({
        ...req.body,
        createdBy: userId,
      });

      // Check if account code already exists
      const existingAccount = await storage.getAccountByCode(accountData.code, accountData.companyId);
      if (existingAccount) {
        return res.status(400).json({ message: "Account code already exists" });
      }

      const account = await storage.createAccount(accountData);
      res.status(201).json(account);
    } catch (error) {
      console.error("Error creating account:", error);
      res.status(500).json({ message: "Failed to create account" });
    }
  });

  app.put('/api/accounts/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const accountData = insertAccountSchema.partial().parse(req.body);
      const account = await storage.updateAccount(id, accountData);
      res.json(account);
    } catch (error) {
      console.error("Error updating account:", error);
      res.status(500).json({ message: "Failed to update account" });
    }
  });

  app.delete('/api/accounts/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteAccount(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting account:", error);
      res.status(500).json({ message: "Failed to delete account" });
    }
  });

  // Journal Entry routes
  app.get('/api/companies/:companyId/journal-entries', isAuthenticated, async (req: any, res) => {
    try {
      const companyId = parseInt(req.params.companyId);
      const limit = req.query.limit ? parseInt(req.query.limit) : undefined;
      const entries = await storage.getJournalEntries(companyId, limit);
      res.json(entries);
    } catch (error) {
      console.error("Error fetching journal entries:", error);
      res.status(500).json({ message: "Failed to fetch journal entries" });
    }
  });

  app.get('/api/journal-entries/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const entry = await storage.getJournalEntry(id);
      if (!entry) {
        return res.status(404).json({ message: "Journal entry not found" });
      }
      res.json(entry);
    } catch (error) {
      console.error("Error fetching journal entry:", error);
      res.status(500).json({ message: "Failed to fetch journal entry" });
    }
  });

  const journalEntryWithDetailsSchema = z.object({
    entry: insertJournalEntrySchema,
    details: z.array(insertJournalEntryDetailSchema),
  });

  app.post('/api/journal-entries', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { entry, details } = journalEntryWithDetailsSchema.parse(req.body);

      // Validate that total debits equal total credits
      const totalDebit = details.reduce((sum, detail) => sum + Number(detail.debit || 0), 0);
      const totalCredit = details.reduce((sum, detail) => sum + Number(detail.credit || 0), 0);

      if (Math.abs(totalDebit - totalCredit) > 0.01) {
        return res.status(400).json({ message: "Total debits must equal total credits" });
      }

      const entryData = {
        ...entry,
        createdBy: userId,
        totalDebit: totalDebit.toString(),
        totalCredit: totalCredit.toString(),
      };

      const journalEntry = await storage.createJournalEntry(entryData, details);
      res.status(201).json(journalEntry);
    } catch (error) {
      console.error("Error creating journal entry:", error);
      res.status(500).json({ message: "Failed to create journal entry" });
    }
  });

  app.put('/api/journal-entries/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const { entry, details } = journalEntryWithDetailsSchema.parse(req.body);

      // Validate that total debits equal total credits
      const totalDebit = details.reduce((sum, detail) => sum + Number(detail.debit || 0), 0);
      const totalCredit = details.reduce((sum, detail) => sum + Number(detail.credit || 0), 0);

      if (Math.abs(totalDebit - totalCredit) > 0.01) {
        return res.status(400).json({ message: "Total debits must equal total credits" });
      }

      const entryData = {
        ...entry,
        totalDebit: totalDebit.toString(),
        totalCredit: totalCredit.toString(),
      };

      const journalEntry = await storage.updateJournalEntry(id, entryData, details);
      res.json(journalEntry);
    } catch (error) {
      console.error("Error updating journal entry:", error);
      res.status(500).json({ message: "Failed to update journal entry" });
    }
  });

  app.delete('/api/journal-entries/:id', isAuthenticated, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteJournalEntry(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting journal entry:", error);
      res.status(500).json({ message: "Failed to delete journal entry" });
    }
  });

  // Financial summary routes
  app.get('/api/companies/:companyId/financial-summary', isAuthenticated, async (req: any, res) => {
    try {
      const companyId = parseInt(req.params.companyId);
      const summary = await storage.getFinancialSummary(companyId);
      res.json(summary);
    } catch (error) {
      console.error("Error fetching financial summary:", error);
      res.status(500).json({ message: "Failed to fetch financial summary" });
    }
  });

  app.get('/api/companies/:companyId/account-balances', isAuthenticated, async (req: any, res) => {
    try {
      const companyId = parseInt(req.params.companyId);
      const balances = await storage.getAccountBalances(companyId);
      res.json(balances);
    } catch (error) {
      console.error("Error fetching account balances:", error);
      res.status(500).json({ message: "Failed to fetch account balances" });
    }
  });

  // Telegram bot routes
  app.get('/api/companies/:companyId/telegram-settings', isAuthenticated, async (req: any, res) => {
    try {
      const companyId = parseInt(req.params.companyId);
      const settings = await storage.getTelegramSettings(companyId);
      res.json(settings || {});
    } catch (error) {
      console.error("Error fetching telegram settings:", error);
      res.status(500).json({ message: "Failed to fetch telegram settings" });
    }
  });

  app.post('/api/telegram-settings', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const settingsData = insertTelegramSettingsSchema.parse({
        ...req.body,
        createdBy: userId,
      });
      const settings = await storage.upsertTelegramSettings(settingsData);
      res.json(settings);
    } catch (error) {
      console.error("Error saving telegram settings:", error);
      res.status(500).json({ message: "Failed to save telegram settings" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  companies,
  accounts,
  journalEntries,
  journalEntryDetails,
  accountBalances,
  telegramSettings,
  type User,
  type UpsertUser,
  type Company,
  type InsertCompany,
  type Account,
  type InsertAccount,
  type JournalEntry,
  type InsertJournalEntry,
  type JournalEntryDetail,
  type InsertJournalEntryDetail,
  type AccountBalance,
  type TelegramSettings,
  type InsertTelegramSettings,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, sum, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Company operations
  getCompanies(userId: string): Promise<Company[]>;
  getCompany(id: number): Promise<Company | undefined>;
  createCompany(company: InsertCompany): Promise<Company>;
  updateCompany(id: number, company: Partial<InsertCompany>): Promise<Company>;
  deleteCompany(id: number): Promise<void>;

  // Account operations
  getAccounts(companyId: number): Promise<Account[]>;
  getAccount(id: number): Promise<Account | undefined>;
  getAccountByCode(code: string, companyId: number): Promise<Account | undefined>;
  createAccount(account: InsertAccount): Promise<Account>;
  updateAccount(id: number, account: Partial<InsertAccount>): Promise<Account>;
  deleteAccount(id: number): Promise<void>;

  // Journal Entry operations
  getJournalEntries(companyId: number, limit?: number): Promise<(JournalEntry & { details: (JournalEntryDetail & { account: Account })[] })[]>;
  getJournalEntry(id: number): Promise<(JournalEntry & { details: (JournalEntryDetail & { account: Account })[] }) | undefined>;
  createJournalEntry(entry: InsertJournalEntry, details: InsertJournalEntryDetail[]): Promise<JournalEntry>;
  updateJournalEntry(id: number, entry: Partial<InsertJournalEntry>, details?: InsertJournalEntryDetail[]): Promise<JournalEntry>;
  deleteJournalEntry(id: number): Promise<void>;

  // Balance operations
  getAccountBalances(companyId: number): Promise<AccountBalance[]>;
  updateAccountBalance(accountId: number, companyId: number): Promise<void>;

  // Financial summary operations
  getFinancialSummary(companyId: number): Promise<{
    totalRevenue: number;
    totalExpenses: number;
    netProfit: number;
    totalAccounts: number;
  }>;

  // Telegram operations
  getTelegramSettings(companyId: number): Promise<TelegramSettings | undefined>;
  upsertTelegramSettings(settings: InsertTelegramSettings): Promise<TelegramSettings>;
}

export class DatabaseStorage implements IStorage {
  // User operations (required for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Company operations
  async getCompanies(userId: string): Promise<Company[]> {
    return await db.select().from(companies).where(eq(companies.createdBy, userId));
  }

  async getCompany(id: number): Promise<Company | undefined> {
    const [company] = await db.select().from(companies).where(eq(companies.id, id));
    return company;
  }

  async createCompany(company: InsertCompany): Promise<Company> {
    const [newCompany] = await db.insert(companies).values(company).returning();
    return newCompany;
  }

  async updateCompany(id: number, company: Partial<InsertCompany>): Promise<Company> {
    const [updatedCompany] = await db
      .update(companies)
      .set({ ...company, updatedAt: new Date() })
      .where(eq(companies.id, id))
      .returning();
    return updatedCompany;
  }

  async deleteCompany(id: number): Promise<void> {
    await db.delete(companies).where(eq(companies.id, id));
  }

  // Account operations
  async getAccounts(companyId: number): Promise<Account[]> {
    return await db
      .select()
      .from(accounts)
      .where(eq(accounts.companyId, companyId))
      .orderBy(asc(accounts.code));
  }

  async getAccount(id: number): Promise<Account | undefined> {
    const [account] = await db.select().from(accounts).where(eq(accounts.id, id));
    return account;
  }

  async getAccountByCode(code: string, companyId: number): Promise<Account | undefined> {
    const [account] = await db
      .select()
      .from(accounts)
      .where(and(eq(accounts.code, code), eq(accounts.companyId, companyId)));
    return account;
  }

  async createAccount(account: InsertAccount): Promise<Account> {
    const [newAccount] = await db.insert(accounts).values(account).returning();
    return newAccount;
  }

  async updateAccount(id: number, account: Partial<InsertAccount>): Promise<Account> {
    const [updatedAccount] = await db
      .update(accounts)
      .set({ ...account, updatedAt: new Date() })
      .where(eq(accounts.id, id))
      .returning();
    return updatedAccount;
  }

  async deleteAccount(id: number): Promise<void> {
    await db.delete(accounts).where(eq(accounts.id, id));
  }

  // Journal Entry operations
  async getJournalEntries(companyId: number, limit = 50): Promise<(JournalEntry & { details: (JournalEntryDetail & { account: Account })[] })[]> {
    const entries = await db
      .select()
      .from(journalEntries)
      .where(eq(journalEntries.companyId, companyId))
      .orderBy(desc(journalEntries.date))
      .limit(limit);

    const entriesWithDetails = await Promise.all(
      entries.map(async (entry) => {
        const details = await db
          .select({
            id: journalEntryDetails.id,
            journalEntryId: journalEntryDetails.journalEntryId,
            accountId: journalEntryDetails.accountId,
            debit: journalEntryDetails.debit,
            credit: journalEntryDetails.credit,
            description: journalEntryDetails.description,
            descriptionArabic: journalEntryDetails.descriptionArabic,
            createdAt: journalEntryDetails.createdAt,
            account: {
              id: accounts.id,
              code: accounts.code,
              name: accounts.name,
              nameArabic: accounts.nameArabic,
              type: accounts.type,
              subType: accounts.subType,
              parentId: accounts.parentId,
              level: accounts.level,
              isParent: accounts.isParent,
              isActive: accounts.isActive,
              companyId: accounts.companyId,
              createdBy: accounts.createdBy,
              createdAt: accounts.createdAt,
              updatedAt: accounts.updatedAt,
            },
          })
          .from(journalEntryDetails)
          .innerJoin(accounts, eq(journalEntryDetails.accountId, accounts.id))
          .where(eq(journalEntryDetails.journalEntryId, entry.id));

        return { ...entry, details };
      })
    );

    return entriesWithDetails;
  }

  async getJournalEntry(id: number): Promise<(JournalEntry & { details: (JournalEntryDetail & { account: Account })[] }) | undefined> {
    const [entry] = await db.select().from(journalEntries).where(eq(journalEntries.id, id));
    if (!entry) return undefined;

    const details = await db
      .select({
        id: journalEntryDetails.id,
        journalEntryId: journalEntryDetails.journalEntryId,
        accountId: journalEntryDetails.accountId,
        debit: journalEntryDetails.debit,
        credit: journalEntryDetails.credit,
        description: journalEntryDetails.description,
        descriptionArabic: journalEntryDetails.descriptionArabic,
        createdAt: journalEntryDetails.createdAt,
        account: {
          id: accounts.id,
          code: accounts.code,
          name: accounts.name,
          nameArabic: accounts.nameArabic,
          type: accounts.type,
          subType: accounts.subType,
          parentId: accounts.parentId,
          level: accounts.level,
          isParent: accounts.isParent,
          isActive: accounts.isActive,
          companyId: accounts.companyId,
          createdBy: accounts.createdBy,
          createdAt: accounts.createdAt,
          updatedAt: accounts.updatedAt,
        },
      })
      .from(journalEntryDetails)
      .innerJoin(accounts, eq(journalEntryDetails.accountId, accounts.id))
      .where(eq(journalEntryDetails.journalEntryId, entry.id));

    return { ...entry, details };
  }

  async createJournalEntry(entry: InsertJournalEntry, details: InsertJournalEntryDetail[]): Promise<JournalEntry> {
    const [newEntry] = await db.insert(journalEntries).values(entry).returning();

    const entryDetails = details.map(detail => ({
      ...detail,
      journalEntryId: newEntry.id,
    }));

    await db.insert(journalEntryDetails).values(entryDetails);

    // Update account balances
    for (const detail of details) {
      await this.updateAccountBalance(detail.accountId, entry.companyId);
    }

    return newEntry;
  }

  async updateJournalEntry(id: number, entry: Partial<InsertJournalEntry>, details?: InsertJournalEntryDetail[]): Promise<JournalEntry> {
    const [updatedEntry] = await db
      .update(journalEntries)
      .set({ ...entry, updatedAt: new Date() })
      .where(eq(journalEntries.id, id))
      .returning();

    if (details) {
      // Delete existing details
      await db.delete(journalEntryDetails).where(eq(journalEntryDetails.journalEntryId, id));

      // Insert new details
      const entryDetails = details.map(detail => ({
        ...detail,
        journalEntryId: id,
      }));

      await db.insert(journalEntryDetails).values(entryDetails);

      // Update account balances
      for (const detail of details) {
        await this.updateAccountBalance(detail.accountId, updatedEntry.companyId);
      }
    }

    return updatedEntry;
  }

  async deleteJournalEntry(id: number): Promise<void> {
    // Get the entry to know which accounts to update
    const entry = await this.getJournalEntry(id);
    if (!entry) return;

    // Delete details first
    await db.delete(journalEntryDetails).where(eq(journalEntryDetails.journalEntryId, id));

    // Delete the entry
    await db.delete(journalEntries).where(eq(journalEntries.id, id));

    // Update account balances
    for (const detail of entry.details) {
      await this.updateAccountBalance(detail.accountId, entry.companyId);
    }
  }

  // Balance operations
  async getAccountBalances(companyId: number): Promise<AccountBalance[]> {
    return await db
      .select()
      .from(accountBalances)
      .where(eq(accountBalances.companyId, companyId));
  }

  async updateAccountBalance(accountId: number, companyId: number): Promise<void> {
    // Calculate total debits and credits for this account
    const result = await db
      .select({
        totalDebit: sum(journalEntryDetails.debit),
        totalCredit: sum(journalEntryDetails.credit),
      })
      .from(journalEntryDetails)
      .innerJoin(journalEntries, eq(journalEntryDetails.journalEntryId, journalEntries.id))
      .where(
        and(
          eq(journalEntryDetails.accountId, accountId),
          eq(journalEntries.companyId, companyId)
        )
      );

    const totalDebit = Number(result[0]?.totalDebit || 0);
    const totalCredit = Number(result[0]?.totalCredit || 0);
    const netBalance = totalDebit - totalCredit;

    // Upsert account balance
    await db
      .insert(accountBalances)
      .values({
        accountId,
        companyId,
        debitBalance: totalDebit.toString(),
        creditBalance: totalCredit.toString(),
        netBalance: netBalance.toString(),
        lastUpdated: new Date(),
      })
      .onConflictDoUpdate({
        target: [accountBalances.accountId, accountBalances.companyId],
        set: {
          debitBalance: totalDebit.toString(),
          creditBalance: totalCredit.toString(),
          netBalance: netBalance.toString(),
          lastUpdated: new Date(),
        },
      });
  }

  // Financial summary operations
  async getFinancialSummary(companyId: number): Promise<{
    totalRevenue: number;
    totalExpenses: number;
    netProfit: number;
    totalAccounts: number;
  }> {
    // Get revenue accounts (type = 'revenue')
    const revenueResult = await db
      .select({
        total: sum(accountBalances.creditBalance),
      })
      .from(accountBalances)
      .innerJoin(accounts, eq(accountBalances.accountId, accounts.id))
      .where(
        and(
          eq(accountBalances.companyId, companyId),
          eq(accounts.type, 'revenue')
        )
      );

    // Get expense accounts (type = 'expenses')
    const expenseResult = await db
      .select({
        total: sum(accountBalances.debitBalance),
      })
      .from(accountBalances)
      .innerJoin(accounts, eq(accountBalances.accountId, accounts.id))
      .where(
        and(
          eq(accountBalances.companyId, companyId),
          eq(accounts.type, 'expenses')
        )
      );

    // Get total number of accounts
    const accountCountResult = await db
      .select({
        count: sql<number>`count(*)`,
      })
      .from(accounts)
      .where(eq(accounts.companyId, companyId));

    const totalRevenue = Number(revenueResult[0]?.total || 0);
    const totalExpenses = Number(expenseResult[0]?.total || 0);
    const netProfit = totalRevenue - totalExpenses;
    const totalAccounts = Number(accountCountResult[0]?.count || 0);

    return {
      totalRevenue,
      totalExpenses,
      netProfit,
      totalAccounts,
    };
  }

  // Telegram operations
  async getTelegramSettings(companyId: number): Promise<TelegramSettings | undefined> {
    const [settings] = await db
      .select()
      .from(telegramSettings)
      .where(eq(telegramSettings.companyId, companyId));
    return settings;
  }

  async upsertTelegramSettings(settings: InsertTelegramSettings): Promise<TelegramSettings> {
    const [result] = await db
      .insert(telegramSettings)
      .values(settings)
      .onConflictDoUpdate({
        target: telegramSettings.companyId,
        set: {
          ...settings,
          updatedAt: new Date(),
        },
      })
      .returning();
    return result;
  }
}

export const storage = new DatabaseStorage();

